import { Component, OnInit } from "@angular/core";
import { Cart } from "src/app/domain/cart.model";
import { Product } from "src/app/domain/product.model";
import { ProductRepository } from "../repository/product.repository";

@Component({
  selector: "carousel-basic-demo",
  templateUrl: "./carousel-basic-demo.html",
  styleUrls: ["./carousel-basic-demo.scss"],
})
export class CarouselBasicDemo implements OnInit {
  public selectedCategory: string = "";

  responsiveOptions: any[] | undefined;

  constructor(private repository: ProductRepository, private cart: Cart) {}
  get products(): Product[] {
    return this.repository.getProducts(this.selectedCategory);
  }
  ngOnInit() {
    this.responsiveOptions = [
      {
        breakpoint: "1199px",
        numVisible: 1,
        numScroll: 1,
      },
      {
        breakpoint: "991px",
        numVisible: 2,
        numScroll: 1,
      },
      {
        breakpoint: "767px",
        numVisible: 1,
        numScroll: 1,
      },
    ];
  }

  getSeverity(status: string) {
    switch (status) {
      case "INSTOCK":
        return "success";
      case "LOWSTOCK":
        return "warning";
      case "OUTOFSTOCK":
        return "danger";
    }
  }

  addProductToCart(product: Product) {
    this.cart.addLine(product);
  }

  get categories(): string[] {
    return this.repository.getCategories();
  }

  changeCategory(newCategory?: string) {
    newCategory ? (this.selectedCategory = newCategory) : "";
  }
}
<div class="card">
  <p-carousel
    [value]="products"
    [numVisible]="3"
    [numScroll]="3"
    [circular]="false"
    [responsiveOptions]="responsiveOptions"
  >
    <ng-template let-product pTemplate="item">
      <div
        class="border-1 surface-border border-round m-2 text-center py-5 px-3"
        [routerLink]="['/product',product.id]"
      >
        <div class="mb-3">
          <img
            [src]="'../../assets/images/' + product.id + '.png'"
            [alt]="product.name"
            class="w-6 shadow-2"
          />
        </div>
        <div>
          <h2 class="mb-1">{{ product.name }}</h2>
          <h3 class="mb-1">{{ product.category }}</h3>
          <h4 class="mt-0 mb-3">{{ product.price | currency:"GBP" }}</h4>

          <p-rating
            [(ngModel)]="product.rating"
            [readonly]="true"
            [cancel]="false"
          ></p-rating>
          <div class="car-buttons mt-5">
            <p-button
              label="Remove"
              styleClass="p-button-danger"
              (click)="cart.removeLine(line)"
            ></p-button>
            <p-button
              [routerLink]="['/product',product.id]"
              icon="pi pi-search"
              styleClass="p-button p-button-rounded mr-2"
              label="Details"
            ></p-button>
          </div>
        </div>
      </div>
    </ng-template>
  </p-carousel>
</div>
